        this.authService.getLogger().verbosePii(`MsalCustomNavigationClient - navigating to newUrl: ${newUrl}`);
        this.router.navigateByUrl(newUrl, { replaceUrl: options.noHistory });

        return Promise.resolve(false);
    }
}
